script:
frappe.ui.form.on("Employee", "validate", function(frm,doctype,name) {
    if(frm.doc.permanent_account_no.length<10){
    	msgprint("PAN number should be at least 10 characters");
            validated = false;
	}
    if(frm.doc.provident_fund_account_no && frm.doc.provident_fund_account_no.length<30){
    	msgprint("Provident Fund Account should be for at least 30 characters");
            validated = false;
	}
    if(frm.doc.esic_no && frm.doc.esic_no.length<30){
    	msgprint("ESIC No should be at least 30 characters.");
            validated = false;
	}
});
