script:
frappe.ui.form.on("Employee", "validate", function(frm,doctype,name) {

    if(frm.doc.permanent_account_no && frm.doc.permanent_account_no.length<10){
    	msgprint("PAN number should be at least 10 characters");
            validated = false;
	}
    if(frm.doc.provident_fund_account_no && frm.doc.provident_fund_account_no.length<30){
    	msgprint("Provident Fund Account should be for at least 30 characters");
            validated = false;
	}
    if(frm.doc.esic_no && frm.doc.esic_no.length<30){
    	msgprint("ESIC No should be at least 30 characters.");
            validated = false;
	}
});

frappe.ui.form.on("Supplier Quotation", "onload", 
	function(frm){
                if(frm.doc.items && frm.doc.items[0].prevdoc_docname){
		    if(frm.doc.items[0].prevdoc_docname){
			frm.set_value("material_request", frm.doc.items[0].prevdoc_docname);
		  }
               }
	});

frappe.ui.form.on("Material Request", "refresh", 
	function(frm){
                if(frm.doc.workflow_state == "Rejected"){
                      var dialog = new frappe.ui.Dialog({
		             title: "Reason",
		             fields: [
			    {"fieldtype": "Text", "label": __("Reason for rejection"), 
                            "fieldname": "reason", "reqd": 1 },
                            {"fieldtype": "Button", "label": __("Update"), "fieldname": "update"},
                          ]
                      });
                      dialog.fields_dict.update.$input.click(function() {
		         args = dialog.get_values();
                         frm.doc.reason_for_rejection = args.reason
                         frm.comments.insert_comment("Material Request Approval", "Rejected because- " + args.reason)
                         frm.save();
                         frm.comments.refresh
                         dialog.hide();

                      })
                      frm.doc.workflow_state = "Draft"
                      frm.save();
                   dialog.show();

       }                 
 })

frappe.ui.form.on("Leave Application", {
	onload: function(frm) {
		frm.set_query("leave_approver_two", function() {
			return {
			query:"erpnext.hr.doctype.leave_application.leave_application.get_approvers",
			filters: {
				   employee: frm.doc.employee
			      }
			};
		});
	},
	leave_approver_two: function(frm) {
           frm.set_value("leave_approver_two_name",frappe.user.full_name(frm.doc.leave_approver_two));
        }
})

cur_frm.add_fetch("customer","customer_code","customer_legal_name");
frappe.ui.form.on("Sales Order", "validate", function(frm,cdt,cdn) {
	total_qty=0;
	for(i=0;i<frm.doc.items.length;i++){
		total_qty+=frm.doc.items[i].qty
	};
	cur_frm.set_value("contract_quantities", total_qty)
});

cur_frm.add_fetch("customer","customer_code","customer_legal_name");
frappe.ui.form.on("Opportunity", "validate", function(frm,cdt,cdn) {
	var eov = (frm.doc.estimated_sale_value*frm.doc.probability)/100;
	if(frm.doc.probability>100 || frm.doc.probability<0){
		msgprint("Probability should be 0 to 100");
		validated = false;
		cur_frm.set_value("estimated_opportunity_value",0);
	}
	else{
		cur_frm.set_value("estimated_opportunity_value",eov);
	}
});
frappe.ui.form.on("Opportunity", "refresh", function(frm,cdt,cdn) {
 	if(frm.doc.customer){
	 	if(cur_frm.doc.title == cur_frm.doc.customer){
	 		var title = frm.doc.customer+"-"+frm.doc.name;
	 		cur_frm.set_value("opportunity_code",title)
	 		cur_frm.save()
	 	}

	}
	else if(frm.doc.lead){
		if(cur_frm.doc.title == cur_frm.doc.lead){
			var title = frm.doc.lead+"-"+frm.doc.name;
	 		cur_frm.set_value("opportunity_code",title)
	 		cur_frm.save()
 		}

	}
});
frappe.ui.form.on("Opportunity", "probability", function(frm,cdt,cdn) {
	var eov = (frm.doc.estimated_sale_value*frm.doc.probability)/100;
	if(frm.doc.probability>100 || frm.doc.probability<0){
		msgprint("Probability should be 0 to 100");
	}
	else{
		cur_frm.set_value("estimated_opportunity_value",eov);
	}
});
